+import { FC, useMemo } from 'react';
+import { Accordion, Points, AddGeoPoint } from '@/molecules';
+import { Segment, Lateral, Point } from '@/entities';
+import { SidebarButton, Button } from '@/atoms';
+import { ExclamationCircleIcon, LocationMarkerIcon, TrashIcon } from '@heroicons/react/solid';
+
+const ACCORDION_BG_COLOR = {
+  open: '#0891B21A',
+  closed: 'white',
+};
+
+export type RouteDetailsProps = {
+  segments: Array<Segment>;
+  laterals: Array<Lateral>;
+  currentRouteInEditMode: number;
+  currentPointInEditMode: number;
+  onAddPoint: (point: Point, isEndPoint?: boolean) => void;
+  onAddLateralPoint: (point: Point, isEndPoint?: boolean) => void;
+  onAddLateralConnectionPoint: () => void;
+  onChangePoint: (point: Point, pointIndex: number) => void;
+  onChangeLateralPoint: (point: Point, pointIndex: number) => void;
+  onDeletePoint: (pointIndex: number) => void;
+  onDeleteLateralPoint: (pointIndex: number) => void;
+  onAddLateral: () => void;
+  onDeleteLateral: (deleteIndex: number) => void;
+  onCurrentRouteInEditModeClick: (routeIndex: number) => void;
+  onCurrentPointInEditModeClick: (pointIndex: number) => void;
+  onSubmit: () => void;
+};
+
+export const RouteDetails: FC<RouteDetailsProps> = ({
+  segments,
+  laterals,
+  currentRouteInEditMode,
+  currentPointInEditMode,
+  onAddPoint,
+  onAddLateralPoint,
+  onAddLateralConnectionPoint,
+  onChangePoint,
+  onChangeLateralPoint,
+  onDeletePoint,
+  onDeleteLateralPoint,
+  onAddLateral,
+  onDeleteLateral,
+  onCurrentRouteInEditModeClick,
+  onCurrentPointInEditModeClick,
+  onSubmit,
+}) => {
+  const isMainLineOpen = currentRouteInEditMode === 0;
+  const mainColor = isMainLineOpen ? 'open' : 'closed';
+
+  const submitButton = useMemo(() => {
+    const isEditingMainRoute = currentRouteInEditMode === 0;
+    const isEditingLateral = currentRouteInEditMode > 0;
+
+    if (isEditingMainRoute) {
+      const routeHasUnoptimizedSegments = segments.some(
+        (segment) => segment.userDefinedPoints.length >= 2 && segment.optimizedPoints.length === 0,
+      );
+
+      if (routeHasUnoptimizedSegments) {
+        return { isEnabled: true, label: 'Optimize Main Line' };
+      }
+
+      if (segments.every((segment) => segment.optimizedPoints.length > 0)) {
+        return { isEnabled: false, label: 'Main Line Optimized' };
+      }
+
+      return { isEnabled: false, label: 'Optimize Main Line' };
+    }
+
+    if (isEditingLateral && laterals[currentRouteInEditMode - 1]) {
+      const lateral = laterals[currentRouteInEditMode - 1];
+      const lateralName = `${lateral.name} ${currentRouteInEditMode}`;
+
+      const lateralHasUnoptimizedSegments = lateral.segments.some(
+        (segment) => segment.userDefinedPoints.length >= 2 && segment.optimizedPoints.length === 0,
+      );
+
+      if (lateralHasUnoptimizedSegments) {
+        return { isEnabled: true, label: `Optimize ${lateralName}` };
+      }
+
+      if (lateral.segments.every((segment) => segment.optimizedPoints.length > 0)) {
+        return { isEnabled: false, label: `${lateralName} Optimized` };
+      }
+
+      return { isEnabled: false, label: `Optimize ${lateralName}` };
+    }
+
+    return { isEnabled: false, label: '' };
+  }, [currentRouteInEditMode, segments, laterals]);
+
+  return (
+    <>
+      <Accordion
+        title="Main Route"
+        backgroundColor={ACCORDION_BG_COLOR[mainColor]}
+        isOpen={isMainLineOpen}
+        onClick={() => {
+          onCurrentRouteInEditModeClick(0);
+          onCurrentPointInEditModeClick(0);
+        }}
+        actions={[
+          {
+            Icon: LocationMarkerIcon,
+            tooltip: 'Modify Route',
+            isDisabled: segments.some((segment) => segment.optimizedPoints.length === 0),
+            onClick: () => {},
+          },
+        ]}
+      >
+        <Points
+          segments={segments}
+          currentPointInEditMode={currentPointInEditMode}
+          onAddPoint={onAddPoint}
+          onChangePoint={onChangePoint}
+          onDeletePoint={onDeletePoint}
+          onCurrentPointInEditModeClick={onCurrentPointInEditModeClick}
+        />
+      </Accordion>
+      {laterals.map((lateral, lateralIndex) => {
+        const isLateralOpen = currentRouteInEditMode === lateralIndex + 1;
+        const lateralColor = isLateralOpen ? 'open' : 'closed';
+
+        return (
+          <div key={`${lateral.name}-${lateralIndex}`}>
+            <Accordion
+              title={
+                <div className="flex items-center gap-x-1">
+                  {`${lateral.name} ${lateralIndex + 1}`}{' '}
+                  {!lateral.isConnected && (
+                    <ExclamationCircleIcon className="text-red-600 h-4 w-4" />
+                  )}
+                </div>
+              }
+              backgroundColor={ACCORDION_BG_COLOR[lateralColor]}
+              isOpen={isLateralOpen}
+              onClick={() => {
+                onCurrentRouteInEditModeClick(lateralIndex + 1);
+                onCurrentPointInEditModeClick(0);
+              }}
+              actions={[
+                {
+                  Icon: LocationMarkerIcon,
+                  tooltip: 'Modify Route',
+                  isDisabled: true,
+                  onClick: () => {},
+                },
+                {
+                  Icon: TrashIcon,
+                  tooltip: 'Delete',
+                  onClick: () => onDeleteLateral(lateralIndex),
+                },
+              ]}
+            >
+              <Points
+                prepend={
+                  !lateral.isConnected && (
+                    <AddGeoPoint
+                      onClick={onAddLateralConnectionPoint}
+                      label="Add Point to Connect Lateral"
+                      variant="danger"
+                      backgroundColor="white"
+                      disabled={segments.every((segment) => segment.optimizedPoints.length === 0)}
+                    />
+                  )
+                }
+                segments={lateral.segments}
+                currentPointInEditMode={currentPointInEditMode}
+                onAddPoint={onAddLateralPoint}
+                onChangePoint={onChangeLateralPoint}
+                onDeletePoint={onDeleteLateralPoint}
+                onCurrentPointInEditModeClick={onCurrentPointInEditModeClick}
+              />
+            </Accordion>
+          </div>
+        );
+      })}
+      <div className="p-4 border-t border-b border-black border-opacity-10">
+        <SidebarButton
+          dataTestId="add-lateral-button"
+          enabledText="Add Lateral"
+          disabledText="Optimize Main Line First"
+          enabledSecondaryText="Add Lateral(s)"
+          isDisabled={segments.some((segment) => segment.optimizedPoints.length === 0)}
+          onClick={onAddLateral}
+        />
+      </div>
+      <div className="mt-auto sticky bottom-0">
+        <Button
+          dataTestId="optimize-line-button"
+          shape="rectangle"
+          display="block"
+          size="lg"
+          label={submitButton.label}
+          disabled={!submitButton.isEnabled}
+          onClick={onSubmit}
+        />
+      </div>
+    </>

