+import { ViewMode } from 'nebula.gl';
+
+class DeleteMode extends ViewMode {
+  // eslint-disable-next-line class-methods-use-this
+  handleClick(e: any, props: any) {
+    const { picks } = e;
+    const { onEdit, data } = props;
+
+    if (picks.length > 0) {
+      // find the line clicked
+      const pick = picks.find((p: any) => p.object.geometry.type === 'LineString');
+      if (pick) {
+        const { index } = pick;
+        const updatedData = { ...data };
+        updatedData.features = updatedData.features.filter(
+          (_: any, featureIndex: number) => featureIndex !== index,
+        );
+        const updatedFeatureIndexes = updatedData.features.map(
+          (_: any, featureIndex: number) => featureIndex,
+        );
+        const editData = {
+          updatedData,
+          editType: 'deleteFeature',
+          editContext: { updatedFeatureIndexes, pickedFeature: pick.object },
+        };
+        onEdit(editData);
+      }
+    }
+  }
+
+  // eslint-disable-next-line class-methods-use-this
+  handlePointerMove({ picks }: any, { onUpdateCursor }: any) {
+    if (picks.find((p: any) => p.object.geometry.type === 'LineString')) {
+      onUpdateCursor('cell');
+    } else {
+      onUpdateCursor(null);
+    }
+  }
+}
+
+export { DeleteMode };

