+import React from 'react';
+import { DashboardNavbar } from '@/molecules';
+import { render, waitFor } from '@testing-library/react';
+import { mount } from 'enzyme';
+
+const baseProps = {
+  avatar: `https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=2&w=256&h=256&q=80`,
+};
+
+const resizeWindow = (width: number) => {
+  Object.defineProperty(window, 'innerWidth', {
+    writable: true,
+    configurable: true,
+    value: width,
+  });
+  window.dispatchEvent(new Event('resize'));
+};
+
+const getResizedComponent = (viewport: {
+  width: number;
+  size: string;
+  dataTestId: string;
+}) => {
+  const { width, size, dataTestId } = viewport;
+
+  resizeWindow(width);
+
+  const sizes: { [key: string]: boolean } = {
+    xs: window.innerWidth < 640,
+    sm: window.innerWidth <= 767 && window.innerWidth >= 640,
+    md: window.innerWidth > 768 && window.innerWidth < 1024,
+  };
+  const dataTestProps: { [key: string]: boolean | string } = { dataTestId };
+
+  let key = 'isDataTestExtraSmallViewPort';
+  if (size === 'sm') key = 'isDataTestSmallViewPort';
+  if (size === 'md') key = 'isDataTestMediumViewPort';
+
+  dataTestProps[key] = sizes[size];
+
+  return mount(<DashboardNavbar {...baseProps} {...dataTestProps} />);
+};
+
+describe('<DashboardNavbar />', () => {
+  beforeAll(() => {
+    Object.defineProperty(window, 'matchMedia', {
+      writable: true,
+      value: jest.fn().mockImplementation((query) => ({
+        matches: false,
+        media: query,
+        onchange: null,
+        addListener: jest.fn(), // Deprecated
+        removeListener: jest.fn(), // Deprecated
+        addEventListener: jest.fn(),
+        removeEventListener: jest.fn(),
+        dispatchEvent: jest.fn(),
+      })),
+    });
+  });
+
+  it('should render', () => {
+    const { asFragment } = render(<DashboardNavbar {...baseProps} />);
+    expect(asFragment()).toMatchSnapshot();
+  });
+
+  it('should hide logo for >=640px and <=767px', () => {
+    const viewports = [
+      {
+        width: 640,
+        size: 'sm',
+        dataTestId: 'hide-logo-element',
+      },
+      {
+        width: 767,
+        size: 'sm',
+        dataTestId: 'hide-logo-element',
+      },
+    ];
+
+    viewports.forEach((viewport) => {
+      const wrapper = getResizedComponent(viewport);
+      const element = wrapper.find(
+        `[data-testid="${viewport.dataTestId}-logo"]`,
+      );
+      expect(element.getDOMNode()).not.toBeVisible();
+    });
+  });
+
+  it('should show logo for viewports <640px and >767px', () => {
+    const viewports = [
+      {
+        width: 639,
+        size: 'xs',
+        dataTestId: 'show-logo-element',
+      },
+      {
+        width: 768,
+        size: 'md',
+        dataTestId: 'show-logo-element',
+      },
+    ];
+
+    viewports.forEach(async (viewport) => {
+      const wrapper = getResizedComponent(viewport);
+      const element = wrapper.find(
+        `[data-testid="${viewport.dataTestId}-logo"]`,
+      );
+      await waitFor(() => {
+        expect(element.getDOMNode()).toBeVisible();
+      });
+    });
+  });
+
+  it('should hide main menu for viewports <640px', () => {
+    const dataTestId = 'hide-menu-element';
+    const wrapper = getResizedComponent({
+      width: 639,
+      size: 'xs',
+      dataTestId,
+    });
+
+    const element = wrapper.find(`[data-testid="${dataTestId}-menu"]`);
+    expect(element.getDOMNode()).not.toBeVisible();
+  });
+
+  it('should show the mobile toggle menu for viewports <640px', () => {
+    const dataTestId = 'show-element';
+    const wrapper = getResizedComponent({
+      width: 500,
+      size: 'xs',
+      dataTestId,
+    });
+
+    const element = wrapper.find(
+      `[data-testid="${dataTestId}-mobile-toggle-btn"]`,
+    );
+
+    expect(element.getDOMNode()).toHaveStyle('display: block');

