+import { getAccessToken } from '@auth0/nextjs-auth0';
+import { IncomingMessage, ServerResponse } from 'http';
+
+export async function redirectUnregisteredUsers(
+  req: IncomingMessage,
+  res: ServerResponse,
+  signUp = false,
+) {
+  let destination = '/';
+  try {
+    const { accessToken } = await getAccessToken(req, res);
+
+    const profileResponse = await fetch(
+      `${process.env.BACKEND_URL}/users/profile`,
+      { headers: { authorization: `Bearer ${accessToken}` } },
+    );
+
+    const responseBody = await profileResponse.text();
+    const profile = JSON.parse(responseBody.trim() || '{}');
+    const onboardingStep = profile.onboardingStep || 1;
+
+    destination =
+      onboardingStep === 4
+        ? '/dashboard'
+        : `/registration/step-${onboardingStep}`;

