+/**
+ * Original source: https://gist.github.com/adamwathan/3b9f3ad1a285a2d1b482769aeb862467
+ * Author: Adam Wathan
+ *
+ */
+import { CSSTransition as ReactCSSTransition } from 'react-transition-group';
+import React, { createContext, useEffect, useRef, ReactNode } from 'react';
+
+const TransitionContext = createContext({
+  parent: {},
+});
+
+function useIsInitialRender() {
+  const isInitialRender = useRef(true);
+  useEffect(() => {
+    isInitialRender.current = false;
+  }, []);
+  return isInitialRender.current;
+}
+
+type CSSTransitionProps = {
+  show?: boolean;
+  enter: string;
+  enterFrom: string;
+  enterTo: string;
+  leave: string;
+  leaveFrom?: string;
+  leaveTo: string;
+  appear?: boolean;
+  children: ReactNode;
+};
+
+function CSSTransition({
+  show,
+  enter = '',
+  enterFrom = '',
+  enterTo = '',
+  leave = '',
+  leaveFrom = '',
+  leaveTo = '',
+  appear,
+  children,
+}: CSSTransitionProps) {
+  const enterClasses = enter.split(' ').filter((s) => s.length);
+  const enterFromClasses = enterFrom.split(' ').filter((s) => s.length);
+  const enterToClasses = enterTo.split(' ').filter((s) => s.length);
+  const leaveClasses = leave.split(' ').filter((s) => s.length);
+  const leaveFromClasses = leaveFrom.split(' ').filter((s) => s.length);
+  const leaveToClasses = leaveTo.split(' ').filter((s) => s.length);
+
+  function addClasses(node: HTMLElement, classes: string | any[]) {
+    return classes.length && node.classList.add(...(classes as any));
+  }
+
+  function removeClasses(node: HTMLElement, classes: string | any[]) {
+    return classes.length && node.classList.remove(...(classes as any));
+  }
+
+  return (
+    <ReactCSSTransition
+      appear={appear}
+      unmountOnExit
+      in={show}
+      addEndListener={(node, done) => {
+        node.addEventListener('transitionend', done, false);
+      }}
+      onEnter={(node: HTMLElement) => {
+        removeClasses(node, [...leaveToClasses]);
+        addClasses(node, [...enterClasses, ...enterFromClasses]);
+      }}
+      onEntering={(node: HTMLElement) => {
+        removeClasses(node, [...enterFromClasses]);
+        addClasses(node, [...enterToClasses]);
+      }}
+      onEntered={(node: HTMLElement) => {
+        removeClasses(node, [...enterClasses]);
+      }}
+      onExit={(node: HTMLElement) => {
+        removeClasses(node, [...enterToClasses]);
+        addClasses(node, [...leaveClasses, ...leaveFromClasses]);
+      }}
+      onExiting={(node: HTMLElement) => {
+        removeClasses(node, [...leaveFromClasses]);
+        addClasses(node, [...leaveToClasses]);
+      }}
+      onExited={(node: HTMLElement) => {
+        removeClasses(node, [...leaveClasses]);
+      }}
+    >
+      {children}
+    </ReactCSSTransition>
+  );
+}
+
+export const Transition = (props: CSSTransitionProps) => {
+  const isInitialRender = useIsInitialRender();
+  const { show, appear } = props;
+  const isChild = show === undefined;
+
+  if (isChild) {
+    return <CSSTransition {...props} />;
+  }
+
+  return (
+    <TransitionContext.Provider
+      value={{
+        parent: {
+          show,
+          isInitialRender,
+          appear,
+        },
+      }}
+    >
+      <CSSTransition appear={appear} show={show} {...props} />
+    </TransitionContext.Provider>
+  );
+};

